import React from 'react';
import styled from 'styled-components';

// import SummonerSearchInput from './SummonerSearchInput';

const SummonerSearchInput = styled.input`
  width: 800px;
  height: 55px;
  border-radius: 8px;
  border: none;
  font-family: 'Montserrat';
  font-size: 24px;
  font-weight: 600;
  color: #3e3939;
  padding: 0 20px;
  &:focus {
    outline: none;
  }
`;

const SubmitButton = styled.input`
  position: absolute;
  right: 0;
  bottom: 0;
  top: 0;
  border-top-right-radius: 8px;
  border-bottom-right-radius: 8px;
  border: none;
  padding: 0 20px;
  text-transform: uppercase;
  color: #fff;
  background: #02a7ff;
  font-weight: 700;
`;

const RealmSelect = styled.select`
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  border-top-left-radius: 8px;
  border-bottom-left-radius: 8px;
  padding: 0px 10px;
  background: #e8e8e8;
  border: none;
  font-size: 24px;
  font-weight: 600;
  color: #3e3939;
`;

export default function SummonerSearchForm() {
  return (
    <form style={{ position: 'relative' }}>
      <select>
        <option value="BR">BR</option>
        <option value="NA">NA</option>
        <option value="EUW">EUW</option>
      </select>
      <SummonerSearchInput type="text" placeholder="Summoner name" />
      <SubmitButton type="submit" value="search" />
    </form>
  );
}
