import Router from 'next/router';

export default async (req, res) => {
  const { summoner } = req.query;

  const response = await fetch(
    `https://br1.api.riotgames.com/lol/summoner/v4/summoners/by-name/${summoner}?api_key=RGAPI-010fd2a0-a370-44eb-acc2-93947b3a2516`
  );
  const data = await response.json();

  // const matches = await fetch(
  //   `https://br1.api.riotgames.com/lol/match/v4/matchlists/by-account/${summonerId}?api_key=RGAPI-010fd2a0-a370-44eb-acc2-93947b3a2516`
  // );

  // const matchesData = await response.json();

  res.statusCode = 200;
  res.setHeader('Content-Type', 'application/json');
  return res.json(data);
  Router.push(`/${summoner}`);
};
